INSERT INTO public.qa (country_id, topic_id, question, answer) VALUES (1, 1, 'Коли відбувається прийом громадян?', 'Прийом громадян відбувається щоденно з 09.30 до 12.30. Крім того, в понеділок прийом  також проводиться з 14.00 до 17.00');
INSERT INTO public.qa (country_id, topic_id, question, answer) VALUES (1, 1, 'Як потрапити на прийом?', 'Прийом відбувається за попереднім записом телефоном +61262472182 та імейлом emb_au@mfa.gov.ua, consul_au@mfa.gov.ua');
INSERT INTO public.qa (country_id, topic_id, question, answer) VALUES (1, 2, 'Чи можливо оформити паспорт громадянина України в посольстві?', 'Посольство здійснює оформлення тільки паспортів громадянина України для виїзду за кордон');
INSERT INTO public.qa (country_id, topic_id, question, answer) VALUES (1, 10, 'Як я можу проголосувати на виборах Президента України або народних депутатів України?', 'Тільки за умови наявності у списках виборців. До списку виборців можна бути включеним у разі перебування на консульському обліку в посольстві.');
INSERT INTO public.qa (country_id, topic_id, question, answer) VALUES (2, 1, 'Яким чином можна потрапити на прийом до консульського відділу?', 'Консульський прийом проводиться у години прийому без попередньої реєстрації (окрім візових питань), Пн. з 9.00 до 12.30. Вт. з 14.00 до 17.30, Чт. та Пт. з 9.00 до 12.30. У середу прийом для громадян, які мають особливі потреби або осіб з дітьми, з 09.00 до 12.30.');
INSERT INTO public.qa (country_id, topic_id, question, answer) VALUES (2, 7, 'Яким документом ми можемо підтвердити українське громадянство нашої дитини, що народилася в Австрії, якщо тільки один з батьків є українцем? ', 'Якщо дитині ще не оформлювався паспорт, для підтвердження її належності до громадянства України подається довідка про реєстрацію особи громадянином України.');
